// CancelPage.js

import React, { useState } from 'react';
import axios from 'axios';

function CancelPage() {
  const [userName, setUserName] = useState('');
  const [roomId, setRoomId] = useState('');
  const [cancellationSuccess, setCancellationSuccess] = useState(false);

  const handleCancelBooking = () => {
    if (userName && roomId) {
      axios.delete(`http://localhost:5000/api/bookings/${roomId}/${userName}`)
        .then(response => {
          setCancellationSuccess(true);
          console.log(response.data.message);
        })
        .catch(error => {
          console.error('Error canceling booking:', error);
          setCancellationSuccess(false);
        });
    } else {
      console.error('Please fill in all required fields');
      setCancellationSuccess(false);
    }
  };

  return (
    <div>
      <h1>Cancel Booking</h1>
      <div>
        <label htmlFor="userNameCancel">Your Name:</label>
        <input type="text" id="userNameCancel" onChange={(e) => setUserName(e.target.value)} />
      </div>
      <div>
        <label htmlFor="roomId">Room ID:</label>
        <input type="text" id="roomId" onChange={(e) => setRoomId(e.target.value)} />
      </div>
      <button onClick={handleCancelBooking}>Cancel Booking</button>
      {cancellationSuccess && (
        <p>Booking successfully canceled!</p>
      )}
    </div>
  );
}

export default CancelPage;
