import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [rooms, setRooms] = useState([]);
  const [selectedRoom, setSelectedRoom] = useState('');
  const [userName, setUserName] = useState('');
  const [email, setEmail] = useState('');
  const [mobileNumber, setMobileNumber] = useState('');
  const [numAdults, setNumAdults] = useState(1); // Assuming at least one adult is required
  const [numKids, setNumKids] = useState(0);

  useEffect(() => {
    axios.get('http://localhost:5000/api/rooms')
      .then(response => setRooms(response.data))
      .catch(error => console.error('Error fetching rooms:', error));
  }, []);

  const handleBookRoom = () => {
    if (selectedRoom && userName && email && mobileNumber) {
      axios.post('http://localhost:5000/api/bookings', {
        room_id: selectedRoom,
        user_name: userName,
        email: email,
        mobile_number: mobileNumber,
        num_adults: numAdults,
        num_kids: numKids,
      })
        .then(response => {
          console.log(response.data.message);
          // You can add additional logic here, e.g., updating state
        })
        .catch(error => console.error('Error booking room:', error));
    } else {
      console.error('Please fill in all required fields');
    }
  };

  return (
    <div>
      <h1>Room Booking App</h1>
      <div>
        <label htmlFor="rooms">Select a room:</label>
        <select id="rooms" onChange={(e) => setSelectedRoom(e.target.value)}>
          <option value="">Select</option>
          {rooms.map(room => (
            <option key={room[0]} value={room[0]}>
              {room[1]}
            </option>
           ))}
        </select>
      </div>
      <div>
        <label htmlFor="userName">Your Name:</label>
        <input type="text" id="userName" onChange={(e) => setUserName(e.target.value)} />
      </div>
      <div>
        <label htmlFor="email">Email:</label>
        <input type="email" id="email" onChange={(e) => setEmail(e.target.value)} />
      </div>
      <div>
        <label htmlFor="mobileNumber">Mobile Number:</label>
        <input type="tel" id="mobileNumber" onChange={(e) => setMobileNumber(e.target.value)} />
      </div>
      <div>
        <label htmlFor="numAdults">Number of Adults:</label>
        <input type="number" id="numAdults" min="1" value={numAdults} onChange={(e) => setNumAdults(e.target.value)} />
      </div>
      <div>
        <label htmlFor="numKids">Number of Kids:</label>
        <input type="number" id="numKids" min="0" value={numKids} onChange={(e) => setNumKids(e.target.value)} />
      </div>
      <button onClick={handleBookRoom}>Book Room</button>,
      <button onClick={handleCancelBooking}>Cancel Booking</button>
    </div>
  );
}

export default App;
