import React, { useState, useEffect } from 'react';
import axios from 'axios';

function CancelBooking() {
  const [roomId, setRoomId] = useState('');
  const [userName, setUserName] = useState('');
  const [cancellationSuccess, setCancellationSuccess] = useState(false);

  const handleCancelBooking = async () => {
    try {
      const response = await axios.delete('http://localhost:5000/api/bookings/' + roomId, {
        data: { user_name: userName },
      });
      setCancellationSuccess(true);
      console.log(response.data.message); // Log success message
    } catch (error) {
      console.error('Error cancelling booking:', error);
      setCancellationSuccess(false);
    }
  };

  return (
    <div>
      <h1>Cancel Booking</h1>
      <div>
        <label htmlFor="roomId">Room ID:</label>
        <input type="text" id="roomId" value={roomId} onChange={(e) => setRoomId(e.target.value)} />
      </div>
      <div>
        <label htmlFor="userName">Your Name:</label>
        <input type="text" id="userName" value={userName} onChange={(e) => setUserName(e.target.value)} />
      </div>
      <button onClick={handleCancelBooking}>Cancel Booking</button>
      {cancellationSuccess && <p>Booking cancelled successfully!</p>}
    </div>
  );
}

export default CancelBooking;
